INSERT INTO public.medicine_info (id, "medicineId",  "language", "shortDescription", "url", "longDescription") VALUES('89f20be3-4269-4991-afb0-87992f8229de', 'be12cb3d-c0d2-440f-b219-6356c051d641', 'de', 'Neo-M-Salbe ad us. vet., Salbe in Injektoren', 'https://www.vetpharm.uzh.ch/TAK/03000000/00032901.02', ' Die Wirkung von Procain-Penicillin auf Streptokokken und nicht Beta-Laktamase-bildende Staphylokokken wird durch Neomycin ergänzt, welches zusätzlich auch gegen die meisten gramnegativen Bakterien und penicillinresistente Beta-Laktamase-bildende Staphylokokken wirkt. Vitamin A wirkt epithelschützend.   '); 
