INSERT INTO public.medicine_info (id, "medicineId",  "language", "shortDescription", "url", "longDescription") VALUES('f574a724-0734-4c0a-8ce0-3591e343bd83', '5fa8ff4b-c75a-4f99-91b6-cf38c78bc6ef', 'de', 'Emorex N Berna ad us. vet., Granulat', 'https://www.vetpharm.uzh.ch/TAK/05000000/00053939.01', ' Neomycin besitzt ein breites antibiotisches Wirkungsspektrum und wirkt sowohl gegen gramnegative wie auch grampositive Keime.   Pharmakokinetik Neomycin wird kaum resorbiert, und die Wirkung bleibt auf den Magen-Darm-Trakt beschränkt.   '); 
