INSERT INTO public.medicine_info (id, "medicineId",  "language", "shortDescription", "url", "longDescription") VALUES('e0f65680-d6a2-4170-9da4-71a01426ece2', '620ed7dd-62d6-4a8c-acd5-e8d6584349fa', 'de', 'Kenostart ad us. vet., Zitzentauchmittel', 'https://www.vetpharm.uzh.ch/TAK/05000000/00059272.01', ' Keno™start ad us. vet. ist gegen Mastitis verursachende Bakterien wirksam (Staphylococcus aureus, Escherichia coli, Streptococcus agalactiae, Streptococcus dysgalactiae, Streptococcus uberis, Corynebakterium bovis). Die Iodaktivität basiert auf einem Redoxvorgang, der Mikroorganismen abtötet und Salze mit bakteriellen Proteinen bildet. Durch die Redoxreaktion werden verschiedene Zellwandbestandteile irreversibel verändert. Es wird angenommen, dass insbesondere Sulfhydrylbindungen in den Zellwänden von Bakterien durch das Iod angegriffen werden.   Pharmakokinetik Das Arzneimittel wird äusserlich angewendet und kaum resorbiert.   '); 
