INSERT INTO public.medicine_info (id, "medicineId",  "language", "shortDescription", "url", "longDescription") VALUES('f12453ca-fa69-4a15-be21-e735cae4a2d5', '6acc69bc-0937-453a-9f7a-7bc666bf08f6', 'de', 'Albex 10% ad us. vet., orale Suspension', 'https://www.vetpharm.uzh.ch/TAK/05000000/00056304.01', ' Albex 10% ad us. vet. verfügt über ein breites Wirkungsspektrum, das Lungenwürmer, Bandwürmer, ausgewachsene Leberegel und Magen-Darm-Rundwürmer und -Eier umfasst. Die Dosierungsvorschrift ist genau zu beachten.   '); 
