INSERT INTO public.medicine_info (id, "medicineId",  "language", "shortDescription", "url", "longDescription") VALUES('c45a0711-5a34-4c8c-8de9-35de55135836', '3eb89a9a-f624-4a19-b6ea-b9d5b27af42e', 'de', 'Cestex 100 mg ad us. vet., Filmtabletten', 'https://www.vetpharm.uzh.ch/TAK/05000000/00053487.03', ' Epsiprantel ist ein Anthelminthikum, das nach einmaliger Verabreichung hoch wirksam ist gegen die üblichen Bandwürmer von Katzen und Hunden. Epsiprantel wirkt direkt auf Bandwürmer. Cestex eliminiert die Bandwürmer meist vollständig (Wirksamkeit > 99%). Aufgrund des Verdauungsprozes­ses ist es möglich, dass im Kot nach der Behandlung keine Bandwurmteile sichtbar sind.   '); 
