INSERT INTO public.medicine_info (id, "medicineId",  "language", "shortDescription", "url", "longDescription") VALUES('21696562-4588-4c29-95ff-4e381cc5b255', 'a88543d4-74f7-4cfc-a93e-49d6dbbdfec4', 'de', 'Nobivac DUCAT ad us. vet.', 'https://www.vetpharm.uzh.ch/TAK/00000000/00001644.VAK', ' Nobivac® DUCAT enthält lebend attenuierte Antigene, welche hauptsächlich für den Katzenschnupfen verantwortlich sind: felines Herpesvirus Typ 1 (FHV-1) und felines Calicivirus (FCV). Der Impfstoff wird subkutan verabreicht, was ein Ausscheiden der Impfviren nach der Impfung verhindert. Geimpfte Katzen entwickeln eine belastbare Immunität gegen Katzenschnupfen, verursacht durch FHV-1 und FCV.   '); 
