INSERT INTO public.medicine_info (id, "medicineId",  "language", "shortDescription", "url", "longDescription") VALUES('9a1f2f7d-65e4-4be8-bab3-e20326e50b4b', '2f8c5f65-abe8-4d9c-971b-d3cc6ccb3dd0', 'de', 'Trimethazol ad us. vet., Injektionslösung', 'https://www.vetpharm.uzh.ch/TAK/05000000/00050361.01', ' Bei der Anwendung von Trimethazol ad us. vet. wird der bekannte Synergismus von Trimethoprim und Sulfamethoxazol ausgenutzt. Die beiden Komponenten wirken auf verschiedene Stellen des Bakterienstoffwechsels ein und blockieren das Enzymsystem an zwei unterschiedlichen Stellen. Sulfamethoxazol wirkt als Antagonist auf die Verwertung der p-Aminobenzoesäure, während das Trimethoprim die für die Nucleinsäuresynthese notwendige Bildung von Folsäure hemmt.   '); 
