INSERT INTO public.medicine_info (id, "medicineId",  "language", "shortDescription", "url", "longDescription") VALUES('ea7d9e2c-35b8-43d2-a0fd-df4d2d6d95f7', 'b389ba1a-355e-4eb5-a3f9-f5c8e258d10e', 'de', 'Api Life Var ad us. vet., Evaporationsplättchen', 'https://www.vetpharm.uzh.ch/TAK/06000000/00060557.01', ' Api Life Var ist eine Mischung aus ätherischen Komponenten. Die synergistischen Effekte von Thymol, Eucalyptusoel, Menthol und Kampfer sollen einen Milbenbefall, im Besonderen mit Varroamilben (Varroa destructor), wirkungsvoll bekämpfen. Durch Verdunsten werden die Wirkstoffe freigesetzt und in der Stockluft angereichert. Durch direkten Kontakt fallen die Milben von den infizierten Bienen ab.   '); 
