INSERT INTO public.medicine_info (id, "medicineId",  "language", "shortDescription", "url", "longDescription") VALUES('0a569ff6-3675-4c54-9d09-6f6ce9f9b9bc', '3268c981-1f2d-4b32-8f65-7d7986c9efd1', 'de', 'Tetrabiotic mite ad us. vet., Uterusobletten', 'https://www.vetpharm.uzh.ch/TAK/04000000/00045454.01', ' Tetrabiotic enthält das Antibiotikum Tetracyclin, welches ein sehr breites Wirkungsspektrum gegen grampositive und gramnegative Bakterien, Mykoplasmen, Rickettsien, Chlamydien, Leptospiren, Actinomyceten und Spirochäten aufweist. Die bakteriostatische Wirkung umfasst somit die meisten im Uterus vorkommenden Erreger.   '); 
