INSERT INTO public.medicine_info (id, "medicineId",  "language", "shortDescription", "url", "longDescription") VALUES('acb60d54-0a46-4aa4-b43b-7bcdb50dc34e', '75dce5fd-91d8-4336-b6f4-65ece85837a4', 'de', 'Blockade ad us. vet., Zitzendesinfektionsmittel', 'https://www.vetpharm.uzh.ch/TAK/05000000/00055996.01', ' Blockade ad us.vet. ist ein gebrauchsfertiges Zitzendesinfektionsmittel auf Jodbasis für den täglichen Gebrauch nach dem Melken. Blockade kombiniert eine hervorragende Hautpflege mit effektiver Desinfektion aufgrund des stabilen Gehalts von frei verfügbarem lod. Es hat einen hautfreundlichen pH-Wert. Blockade wird direkt nach dem Melken im Tauchverfahren angewendet.   '); 
