INSERT INTO public.medicine_info (id, "medicineId",  "language", "shortDescription", "url", "longDescription") VALUES('36bda816-da32-4441-9877-c2daad002bfa', '5ab6ad59-267e-4752-9cb3-029a33a9eb12', 'de', 'Eazi-breed™ CIDR G ad us. vet., Intravaginalpessar', 'https://www.vetpharm.uzh.ch/TAK/05000000/00052284.01', ' Die intravaginale Spange Eazi-Breed besteht aus einem Nylon-Kern mit einer Silikon-Ummantelung, in welche Progesteron eingebettet ist. Progesteron diffundiert in kontrollierter Menge aus dem Silikon und wird resorbiert. Dadurch wird die Freisetzung von LH und FSH in der Hypophyse gehemmt. Nach Entfernung der Spange sinkt der Progesteronspiegel schnell, wodurch Follikelreifung und Brunst ausgelöst werden.   '); 
