INSERT INTO public.medicine_info (id, "medicineId",  "language", "shortDescription", "url", "longDescription") VALUES('e1703ef6-469f-41dc-a077-f83609e0d145', '47cec72a-107d-4c8a-8864-00aa9b2b94cc', 'de', 'Nobilis Paramyxo ad us. vet.', 'https://www.vetpharm.uzh.ch/TAK/00000000/00001627.VAK', ' Nobilis® Paramyxo P201 wird durch Vermehrung des PPMV-1-Stammes P201 auf embryonierten Hühnereiern hergestellt. Der Impfstoff enthält kein Konservierungsmittel. Er liegt als ölige Emulsion vor, wodurch die immunogene Wirkung verstärkt wird. In einem Versuch konnte gezeigt werden, dass geimpfte Tiere nach Challenge signifikant weniger Viren ausscheiden als nicht geimpfte Tiere.   '); 
