INSERT INTO public.medicine_info (id, "medicineId",  "language", "shortDescription", "url", "longDescription") VALUES('1671476f-6882-4739-9a44-7909911e7ad6', 'd6d7d20c-ccf7-4b43-b62a-685b8a222f06', 'de', 'Vetisept Obletten ad us. vet.', 'https://www.vetpharm.uzh.ch/TAK/05000000/00050514.01', ' Polyvinylpyrrolidon-Jod (PVP-Jod) ist ein organischer Komplex aus Jod und einem wasserlöslichen Polymer. PVP wirkt als Lösungsvermittler, Stabilisator und Schutzkolloid. Es bewirkt, dass das Jod erst am Applikationsort freigesetzt wird. PVP-Jod hat das volle und breite Wirkungsspektrum von Jod, ist jedoch weniger toxisch, stabiler und wasserlöslich. Es wirkt bakterizid, fungizid, protozid, sporozid und viruzid. Die keimtötende Wirkung tritt sofort ein und hält lange an. Solange die goldbraune Farbe sichtbar ist, bleibt Vetisept voll wirksam. Es reizt weder Haut noch Schleimhäute. Der desinfizierende Effekt wird durch kaltes Wasser nicht herabgesetzt und bleibt auch in Gegenwart von Seifen, Detergentien und Fetten erhalten. Flecken auf Händen und Textilien können mit Wasser und Seife leicht entfernt werden.   '); 
INSERT INTO public.medicine (id, "searchTerm") VALUES('2e6f77ef-7287-4c68-b16c-6d0ec7c8c0bc', 'B-COL'); 
INSERT INTO public.medicine_info (id, "medicineId",  "language", "shortDescription", "url", "longDescription") VALUES('dd510928-9733-48f0-bdd8-dc6a0aa7ea86', '2e6f77ef-7287-4c68-b16c-6d0ec7c8c0bc', 'de', 'B-COL 500 ad us. vet., Arzneimittelvormischung', 'https://www.vetpharm.uzh.ch/TAK/04000000/00040074.02', ' Das Colistin (Polymyxin E) ist ein bakterizid wirkendes Antibiotikum aus der Familie der Polypeptidantibiotika.   Polymyxine sind wirksam gegen gramnegative Bakterien des Magen-Darm-Traktes. Das Wirkungsspektrum beinhaltet in vitro E. coli, Salmonella, Klebsiella, Enterobacter und Shigella spp.   Die Wirkung der Polymyxine beruht auf einer Bindung an Lipopolysaccharide der Lipidmembranen und bewirkt somit eine Störung der Permeabilität der äusseren Zellmembran gramnegativer Keime. Resistenzen der Zielkeime gegenüber Colistin werden selten beobachtet.   Pharmakokinetik Colistin wird nach oraler Gabe nur in sehr geringem Ausmass aus dem intakten Magen-Darm-Trakt resorbiert.   '); 
