INSERT INTO public.medicine_info (id, "medicineId",  "language", "shortDescription", "url", "longDescription") VALUES('6ae8a048-3e57-4671-af14-2df3e23af4aa', 'f9d4d00b-d126-4373-b845-a08aa70898a8', 'de', 'Erysen Parvo ad us. vet., Injektionssuspension für Schweine', 'https://www.vetpharm.uzh.ch/TAK/00000000/00001740.VAK', ' Erysen Parvo ist ein inaktivierter Virus- und Bakterienimpfstoff für Schweine (porcines Parvovirus und Erysipelothrix rhusiopathiae). Er stimuliert die Immunität gegen die enthaltenen Erreger und schützt Muttertiere vor Rotlauf und ihre Föten vor einer intrauterinen Infektion mit dem porcinen Parvovirus (SMEDI).   '); 
