INSERT INTO public.medicine_info (id, "medicineId",  "language", "shortDescription", "url", "longDescription") VALUES('55646404-89a1-44be-a13e-df30437ea66c', 'cbc04664-c211-4ab4-bfeb-d3e8d190db9a', 'de', 'Duowin Line On M ad us. vet., Lösung', 'https://www.vetpharm.uzh.ch/TAK/05000000/00054798.02', ' Permethrin ist ein Molekül der Familie der synthetischen Pyrethroide und zeichnet sich durch seine schnelle repulsive, insektizide und akarizide Wirkung aus. Das Molekül hemmt die Übertragung von Nervenimpulsen: Pyrethroide verändern die Durchlässigkeit der Axonmembran für Na+-Ionen. Pyriproxyfen ist ein Analogon des Juvenil-Hormons der Insekten. Es verhindert die Entstehung von adulten Insekten, indem es die biologische Entwicklung der Eier und Larven blockiert.   '); 
