INSERT INTO public.medicine_info (id, "medicineId",  "language", "shortDescription", "url", "longDescription") VALUES('8047e3f4-0b81-40ee-8a22-fac44677da39', '7e6f81e4-794f-41d9-8695-d7644794be4f', 'de', 'Duowin Line On S ad us. vet., Lösung', 'https://www.vetpharm.uzh.ch/TAK/05000000/00054798.01', ' Permethrin ist ein Molekül der Familie der synthetischen Pyrethroide und zeichnet sich durch seine schnelle repulsive, insektizide und akarizide Wirkung aus. Das Molekül hemmt die Übertragung von Nervenimpulsen: Pyrethroide verändern die Durchlässigkeit der Axonmembran für Na+-Ionen. Pyriproxyfen ist ein Analogon des Juvenil-Hormons der Insekten. Es verhindert die Entstehung von adulten Insekten, indem es die biologische Entwicklung der Eier und Larven blockiert.   '); 
