INSERT INTO public.medicine_info (id, "medicineId",  "language", "shortDescription", "url", "longDescription") VALUES('2a9f8dfe-c0d4-4c11-9f04-b213ef747b6a', '0d3ca74a-36df-4cdc-8992-2503c2244433', 'de', 'Josty Antiparasit Halsband ad us. vet., für grosse Hunde', 'https://www.vetpharm.uzh.ch/TAK/05000000/00054991.03', ' Josty, Antiparasitenhalsband, schützt Hunde wirksam gegen Flöhe, Zecken und Läuse. Die Flöhe sind in einigen Stunden tot. Wirkungszeit 4 Monate, also 120 Tage effektiver Schutz. Kein Wirkungsverlust nach Kontakt mit Wasser. Keine Störung des Geruchssinns bei Jagdhunden. Läuft nicht aus.   '); 
