INSERT INTO public.medicine_info (id, "medicineId",  "language", "shortDescription", "url", "longDescription") VALUES('28d23595-c3b7-436a-a06c-b66efd4cc131', '2f3a3583-8203-4ded-b5e6-453368497d8b', 'de', 'Animedazon Spray ad us. vet., Spray', 'https://www.vetpharm.uzh.ch/TAK/06000000/00066436.01', ' Chlortetracyclin wirkt bakteriostatisch durch Hemmung der bakteriellen Proteinsynthese. Dadurch werden lebenswichtige Funktionen der Bakterien gestört.   Pharmakokinetik Nach Anwendung von Chlortetracyclinspray auf der Haut kommt es zu einer vernachlässigbaren Resorption des Wirkstoffes. Deshalb wirkt Animedazon Spray nur lokal; eine systemische Wirkung oder unerwünschte Nebenwirkungen treten nicht auf.   '); 
