INSERT INTO public.medicine_info (id, "medicineId",  "language", "shortDescription", "url", "longDescription") VALUES('2787f029-9f22-4526-b897-d997dbcd7abe', '8c845793-1296-4ac7-b3c1-b6cf78979ddd', 'de', 'Insol Dermatophyton ad us. vet.', 'https://www.vetpharm.uzh.ch/TAK/00000000/00001500.VAK', ' Die Verabreichung des Impfstoffes bewirkt die Ausbildung einer Immunität bei Pferden, Hunden und Katzen gegen Dermatophytosen, verursacht durch Trichophyton verrucosum, Trichophyton mentagrophytes, Trichophyton sarkisovii, Trichophyton equinum, Mikrosporum canis und Nannizzia gypsea. Die erzeugte Immunität ist hauptsächlich eine zellvermittelte Immunantwort.   '); 
