INSERT INTO public.medicine_info (id, "medicineId",  "language", "shortDescription", "url", "longDescription") VALUES('e3da208a-6c9d-41a8-8569-15bb2e755b0b', '5a31c928-14de-4703-a568-5f681d2ca9d0', 'de', 'Esconarkon ad us. vet., Injektionslösung', 'https://www.vetpharm.uzh.ch/TAK/05000000/00055815.01', ' Die intravenöse oder intraperitoneale Injektion von Pentobarbital ist eine schmerzlose und sichere Methode zur Euthanasie von Gross- und Kleintieren. Die Tiere fallen schnell in einen tiefen Schlaf, der bei Warmblütern rasch, schmerz- und reflexlos und ohne Exzitationen in den Tod durch Herz- und Atemstillstand übergeht. Bei wechselwarmen Tieren tritt der Tod, je nach Resorptions- und Stoffwechselgeschwindigkeit, mehr oder weniger verzögert ein.   '); 
