INSERT INTO public.medicine_info (id, "medicineId",  "language", "shortDescription", "url", "longDescription") VALUES('2ad72ceb-a923-4a09-8f09-b8d0e4c742e1', '53514ebd-ed98-44c3-9266-9fa50dd41cbb', 'de', 'Dicural 15 mg ad us. vet., Manteltabletten', 'https://www.vetpharm.uzh.ch/TAK/05000000/00054235.01', ' Dicural® ist eine Manteltablette mit dem Wirkstoff Difloxacinhydrochlorid und einem sehr schmackhaften Überzug, welcher eine exzellente Akzeptanz bei Hunden bewirkt. Difloxacinhydrochlorid ist ein Arylfluorchinolon-Bakterizid. Die antimikrobielle Breitbandwirkung richtet sich gegen die meisten gramnegativen sowie eine Reihe von grampositiven Mikroorganismen und gegen Mykoplasmen.   '); 
