INSERT INTO public.medicine_info (id, "medicineId",  "language", "shortDescription", "url", "longDescription") VALUES('ea8a23f5-6bef-484c-9d6c-32db015100c4', '80e1585c-cb1f-4eec-b836-97bf63d4c8fa', 'de', 'Cloxa-coli ad us. vet., Suspension in Injektoren', 'https://www.vetpharm.uzh.ch/TAK/04000000/00042270.03', ' Cloxacillin weist eine hervorragende Wirksamkeit gegenüber grampositiven Keimen auf. Es ist insbesondere gegen Penicillinase bildende Staphylokokken wirksam, die bei Mastitiden angetroffen werden. Colistin wirkt gezielt gegen gramnegative Keime und stellt deshalb die adäquate Ergänzung zum Cloxacillin dar. Die Kombination beider Antibiotika wirkt gegen die meisten für Mastitiden verantwortlichen Keime. Resistenzbildung ist selten.   '); 
