INSERT INTO public.medicine_info (id, "medicineId",  "language", "shortDescription", "url", "longDescription") VALUES('742ef687-c29c-473a-9b75-fe588d92d0ad', 'f1bcfe19-e073-4772-a66a-eda65bfb245c', 'de', 'Formivar 85% ad us. vet., Lösung', 'https://www.vetpharm.uzh.ch/TAK/06000000/00062888.01', ' Ameisensäure führt zu einer Atemhemmung und Übersäuerung der Varroamilben. Diese reagieren empfindlicher auf Ameisensäure als Bienenlarven und adulte Bienen.   '); 
INSERT INTO public.medicine (id, "searchTerm") VALUES('44919f0a-6314-4ea0-819e-010289b7afe6', 'Clamoxyl'); 
INSERT INTO public.medicine_info (id, "medicineId",  "language", "shortDescription", "url", "longDescription") VALUES('e8c422a1-630f-468d-af61-2f6a526663d6', '44919f0a-6314-4ea0-819e-010289b7afe6', 'de', 'Clamoxyl ad us. vet., ölige Injektionssuspension', 'https://www.vetpharm.uzh.ch/TAK/04000000/00045176.01', ' Amoxycillin ist ein aus dem Penicillinkern entwickeltes halbsynthetisches Penicillin. Seine wesentlichsten Eigenschaften sind: - Breites Wirkungsspektrum, welches eine Vielzahl von gramnegativen und grampositiven Keimen umfasst. - Rasche therapeutische Wirkung. - Sehr schwache Toxizität und dadurch gute systemische Verträglichkeit.   Pharmakokinetik Amoxicillin wird durch die Nieren ausgeschieden. Im Nierengewebe und im Urin können deshalb sehr hohe Amoxicillin-Spiegel erreicht werden.   '); 
