INSERT INTO public.medicine (id, "searchTerm") VALUES('a1db7392-e0c3-4df0-a113-62bd41709475', 'Terralon'); 
INSERT INTO public.medicine_info (id, "medicineId",  "language", "shortDescription", "url", "longDescription") VALUES('8c35c017-4c4c-46ae-b324-6779ad7b90f4', 'a1db7392-e0c3-4df0-a113-62bd41709475', 'de', 'Terralon 20% LA ad us. vet., Injektionslösung', 'https://www.vetpharm.uzh.ch/TAK/04000000/00049248.01', ' Terralon® 20% LA intramuskulär verabreicht, ist ein Breitbandantibiotikum mit Langzeitwirkung. Die meisten gramnegativen und grampositiven Keime werden durch Terralon® 20% LA erfasst (auch Rickettsien, Protozoen, Chlamydien und Mykoplasmen). Terralon® 20% LA kann sowohl therapeutisch wie auch prophylaktisch angewendet werden. Die Verabreichung von Terralon 20% LA empfiehlt sich in allen Fällen, bei denen eine mehrtägige antibiotische Versorgung angezeigt ist, die Therapie jedoch nicht täglich durchgeführt werden kann.   '); 
