INSERT INTO public.medicine_info (id, "medicineId",  "language", "shortDescription", "url", "longDescription") VALUES('249642a8-fdc3-455b-9c99-93a77e501a7f', '1dd40e1d-520f-4627-88f7-b0cdd65e4d0c', 'de', 'Contralac 5 ad us. vet., Tabletten', 'https://www.vetpharm.uzh.ch/TAK/04000000/00049868.01', ' Metergolin ist ein synthetisches Derivat von Ergolin. Es besitzt eine stark ausgeprägte hemmende Wirkung auf die Prolactinsekretion. Es hat keine dopaminerge Wirkung. Daher bleiben deren unliebsame Nebenwirkungen bei der Medikation aus.   '); 
