INSERT INTO public.medicine_info (id, "medicineId",  "language", "shortDescription", "url", "longDescription") VALUES('d49fa46c-08fb-465b-a269-83e3671513d5', 'a16721a3-1458-4bee-8e36-695b0acc3507', 'de', 'Bovilis Blue-4 ad us. vet.', 'https://www.vetpharm.uzh.ch/TAK/00000000/00001752.VAK', ' Rinder Zur aktiven Immunisierung von Rindern zur Prävention einer Virämie**, die durch das Virus der Blauzungenerkrankung des Serotyps 4 verursacht wird.   Beginn der Immunität: 21 Tage nach Abschluss der Grundimmunisierung Dauer der Immunität: 1 Jahr nach der Grundimmunisierung   Schafe Zur aktiven Immunisierung von Schafen zur Prävention einer Virämie**, die durch das Virus der Blauzungenerkrankung des Serotyps 4 verursacht wird. **(Schwellenwert-Zyklus) Ct-Wert ≥ 36, der durch eine validierte RT-PCR-Methode ermittelt wurde und anzeigt, dass kein Virusgenom vorhanden ist.)   Beginn der Immunität: 21 Tage nach Abschluss der Grundimmunisierung Dauer der Immunität: 1 Jahr nach der Grundimmunisierung   '); 
