INSERT INTO public.medicine_info (id, "medicineId",  "language", "shortDescription", "url", "longDescription") VALUES('aee5e722-09e9-49bd-9ea9-675b5249c913', '952a5b61-9094-4261-a44e-ed6ca1def81c', 'de', 'Lidocain 2% Streuli ad us. vet., Injektionslösung', 'https://www.vetpharm.uzh.ch/TAK/05000000/00050564.02', ' Lidocain ist ein Lokalanästhetikum vom Säureamid-Typ, dessen Wirkungseintritt infolge seiner hohen Diffusionsfähigkeit sehr schnell erfolgt. Es besitzt gleichzeitig eine anhaltende Wirkung mit sehr tiefer Anästhesie. Die Wirkung von Lidocain ist um ein vielfaches stärker als diejenige von Procain und setzt bereits innerhalb von 1 - 5 Minuten bei Leitungs- und Infiltrationsanästhesien ein. Die Anästhesie hält für ca. 60 - 90 Minuten an. Lidocain ist infolge seiner geringen Toxizität lokal und allgemein gut verträglich.   '); 
