INSERT INTO public.medicine_info (id, "medicineId",  "language", "shortDescription", "url", "longDescription") VALUES('e5e397e1-e4a4-423c-a4e6-51f83b826590', '39683d63-7ea5-4e5d-bf16-9587e19345ed', 'de', 'Lorasol GL 0,75% ad us. vet., Zitzentauchmittel', 'https://www.vetpharm.uzh.ch/TAK/04000000/00041531.02', ' Das aus Lorasol GL 0,75% Konzentrat hergestellte Zitzendesinfektionsmittel wirkt abtötend auf euterpathogene Bakterien. Der Glyzerinzusatz verleiht der Tauchlösung hautpflegende Eigenschaften.   '); 
