INSERT INTO public.medicine_info (id, "medicineId",  "language", "shortDescription", "url", "longDescription") VALUES('ad938d78-7fae-4b58-b090-5973b3e63219', '6a38b96f-cffa-4ff0-ba36-9620c0034c50', 'de', 'Bovilis Blue-8 ad us. vet., Injektionssuspension für Rinder und Schafe', 'https://www.vetpharm.uzh.ch/TAK/00000000/00001756.VAK', ' Rinder Zur aktiven Immunisierung von Rindern ab einem Alter von 2,5 Monaten zur Verhinderung einer Virämie**, die durch das Virus der Blauzungenerkrankung des Serotyps 8 verursacht wird.   Beginn der Immunität: 31 Tage nach Abschluss der Grundimmunisierung. Dauer der Immunität: 1 Jahr nach der Grundimmunisierung.   Schafe Zur aktiven Immunisierung von Schafen ab einem Alter von 2,5 Monaten zur Verhinderung einer Virämie** und zur Verringerung klinischer Symptome, die durch das Virus der Blauzungenerkrankung des Serotyps 8 verursacht werden.   **(Schwellenwert-Zyklus) Ct-Wert ≥ 36, der durch eine validierte RT-PCR-Methode ermittelt wurde und anzeigt, dass kein Virusgenom vorhanden ist.   Beginn der Immunität: 20 Tage nach Abschluss der Grundimmunisierung. Dauer der Immunität: 1 Jahr nach der Grundimmunisierung.   '); 
