INSERT INTO public.medicine_info (id, "medicineId",  "language", "shortDescription", "url", "longDescription") VALUES('e1627094-5ab0-453c-a0e8-db518ba232b6', 'f024fcbb-fa16-416c-81a7-ec639e1e78a8', 'de', 'Prostavet ad us. vet., Injektions­lösung', 'https://www.vetpharm.uzh.ch/TAK/05000000/00052825.01', ' Etiproston ist ein Prostaglandin-F2-alpha Analog mit ausgeprägten luteolytischen und die glatte Muskulatur des Uterus beeinflussenden Eigenschaften. Daraus ergeben sich folgende Einsatzmöglichkeiten bei Kühen und Rindern: - Oestrusinduktion und Brunstsynchronisation - die Entleerung von pathologischem Gebärmutter­inhalt zu erleichtern und dadurch bedingte Infek­tionen zu vermeiden.   '); 
