INSERT INTO public.medicine_info (id, "medicineId",  "language", "shortDescription", "url", "longDescription") VALUES('e9943a65-1330-4e21-8bf4-d87ff89da477', 'f6aed6c8-68a7-4043-9b84-a5aa48783e35', 'de', 'Poulvac IB Primer ad us. vet.', 'https://www.vetpharm.uzh.ch/TAK/00000000/00001398.VAK', ' Impfstoff auf Basis der lebenden, infektiösen Bronchitis-Viren H120 und D274, die respektive dem Massachusetts-Typ und dem Varianten-Typ D207 IB angehören.   Der Impfstoff wird auf bebrüteten S.P.F.-Eiern (frei von spezifizierten pathogenen Mikroorganismen) gezüchtet und ist getestet worden auf die Abwesenheit von pathogenen Geflügelviren, P.P.L.O. (Myc. gallisepticum, Myc. synoviae, Myc. meleagridis) und anderen unerwünschten Krankheitskeimen.   '); 
