INSERT INTO public.medicine_info (id, "medicineId",  "language", "shortDescription", "url", "longDescription") VALUES('990849b3-b9f6-4292-956f-01e7dd4c848d', '1201cd4f-3de4-4641-b579-6f92e9e97d6f', 'de', 'Canileish ad us. vet.', 'https://www.vetpharm.uzh.ch/TAK/00000000/00001712.VAK', ' Die Impfung führt zu einer zellvermittelten Immunität, welche sich äussert durch - die Bildung von spezifischen IgG2 Antikörpern, die gegen durch Leishmania infantum sezernierte Proteine gerichtet sind - die Steigerung der leishmaniziden Aktivität von Makrophagen - eine T-Zell Lymphoproliferation mit Sekretion von γ-Zytokinen - eine aktive T-Zell-vermittelte Immunantwort, welche gegen Leishmania Antigene gerichtet ist (Hauttest)   Daten zur Wirksamkeit haben gezeigt, dass das Risiko (Wahrscheinlichkeitsquotient) eine aktive Infektion bzw. eine klinische Erkrankung zu entwickeln für einen geimpften Hund 3,6 mal bzw. 4 mal geringer ist als für einen nicht geimpften Hund. Die Wirksamkeit des Impfstoffes wurde an Hunden nachgewiesen, die in Gebieten mit hohem Infektionsdruck einer mehrfachen natürlichen Belastung durch den Parasiten ausgesetzt waren.   '); 
