INSERT INTO public.medicine_info (id, "medicineId",  "language", "shortDescription", "url", "longDescription") VALUES('19412709-5ac2-4d3b-aca1-e06b8cba002a', '9a0f7bc6-a40b-4021-8a60-04aa8c9b4a12', 'de', 'Energidex ad us. vet., hypertonische Lösung', 'https://www.vetpharm.uzh.ch/TAK/04000000/00042347.01', ' Diese Kombination verbindet die Vorteile des schnell und leicht resorbierbaren Energiespenders Dextrosum mit dem langsam und verlängert wirkenden Sorbitolum.   Indikationen Wegen seiner Zusammensetzung kann Energidex bei sehr vielen '); 
