INSERT INTO public.medicine_info (id, "medicineId",  "language", "shortDescription", "url", "longDescription") VALUES('41e85d7d-91ef-409c-96f6-178310cf7f95', 'b9cabb27-27d4-469b-a17b-a92dd4ad6d5d', 'de', 'Vetagent ad us. vet., Injektionslösung', 'https://www.vetpharm.uzh.ch/TAK/04000000/00046039.01', ' Gentamicin ist ein bakterizid wirkendes Antibiotikum aus der Gruppe der Aminoglykoside und hat ein breites Wirkungsspektrum. Es ist deshalb besonders bei Infektionen mit sogenannten Problemkeimen wie Pseudomonas, Proteus, E. coli, Klebsiellen und Staphylokokken indiziert.   Pharmakokinetik Der therapeutische Blutspiegel wird etwa 1 Stunde nach der Injektion erreicht und hält 6 - 8 Stunden an. Da Gentamicin unverändert via Nieren ausgeschieden wird, kann der Gentamicinspiegel im Urin den Blutspiegel um ein Vielfaches übersteigen.   '); 
