INSERT INTO public.medicine_info (id, "medicineId",  "language", "shortDescription", "url", "longDescription") VALUES('bc6b3689-fe5e-43c5-92b7-f30db1cbdaf6', '006aeafb-6d55-40e5-8ced-e2a13b74bbe1', 'de', 'Gentapen ad us. vet., Euterinjektoren', 'https://www.vetpharm.uzh.ch/TAK/04000000/00049910.02', ' Gentapen-Suspension und -Euterinjektoren enthalten als antimikrobielle Wirkstoffe Penicillin und Gentamicin. Penicillin gilt aufgrund seiner guten Wirksamkeit und ausgezeichneten Verträglichkeit weiterhin als Mittel der Wahl zur Behandlung von Infektionen mit Streptokokken, einschliesslich Gelb-Galt-Streptokokken und Penicillin-empfindlichen Staphylokokken. Gentamicin ist besonders gut geeignet, um Infektionen mit Penicillin-resistenten Staphylokokken und E. coli wirksam zu bekämpfen, da bei diesen Keimen praktisch keine Resistenzen dagegen vorkommen. Gentapen-Suspension und ‑Eu­terinjektoren erfassen daher ein besonders breites Spektrum an Mastitiserregern. Dank der ausgereiften Formulierung sind beide Präparate sehr gut verträglich.   '); 
