INSERT INTO public.medicine_info (id, "medicineId",  "language", "shortDescription", "url", "longDescription") VALUES('010f82e8-9850-4a76-9ee7-45c22e864c17', 'e2552bf0-b76c-48b9-b8ae-37fa73d43dc4', 'de', 'Josty Antiparasit Classic ad us. vet., Halsband für Katzen', 'https://www.vetpharm.uzh.ch/TAK/05000000/00054991.01', ' Josty, Antiparasitenhalsband, schützt Katzen wirksam vor Flöhen. Sie sind in wenigen Stunden tot. Wirkungszeit 4 Monate, also 120 Tage effektiver Schutz. Kein Wirkungsverlust nach Kontakt mit Wasser. Läuft nicht aus.   '); 
