INSERT INTO public.medicine_info (id, "medicineId",  "language", "shortDescription", "url", "longDescription") VALUES('5e0b0fcd-dac3-4690-9f0f-5fa8f6771c2a', 'acaa3a0e-306d-4807-bc26-1c24e4163428', 'de', 'Purevax Rabies ad us. vet.', 'https://www.vetpharm.uzh.ch/TAK/00000000/00001747.VAK', ' Impfstoff gegen eine Tollwutinfektion. Der Impfstamm vCP65 ist eine Rekombinante des Kanarienpockenvirus, die das Glykoprotein G-Gen des Tollwutvirus exprimiert. Nach der Impfung exprimiert das Virus, ohne sich in der Katze zu vermehren, das für den Impfschutz verantwortliche Protein und führt so zur Ausbildung einer aktiven Immunität gegen Tollwut bei der Katze.   '); 
