INSERT INTO public.medicine_info (id, "medicineId",  "language", "shortDescription", "url", "longDescription") VALUES('b93ed98c-72ef-4f28-a259-5bf1e523ee2a', '43609a9e-0fd0-42c1-b0d5-1157a473bd32', 'de', 'Evant ad us. vet., Suspension und Lösungsmittel für orales Spray für Hühner', 'https://www.vetpharm.uzh.ch/TAK/00000000/00001764.VAK', ' Stimuliert eine aktive Immunität gegen durch Eimeria acervulina, Eimeria maxima, Eimeria mitis, Eimeria praecox und Eimeria tenella verursachte Kokzidiose   '); 
